import "@typespec/http";

using Http;
@service(#{ title: "Product Service" })
namespace DemoService;

model Product {
  id: uint32;
  name: string;
  category: ProductCategory;
}

enum ProductCategory {
  Electronics,
  Books,
  Clothing,
  Home
}

@route("/products")
@tag("Products")
interface Products {
  /** List products */
  @get list(): Product[];

  /** Read products */
  @get read(@path id: string): Product;

  /** Create a product */
  @post create(@body body: Product): Product;

  /** Update a product */
  @patch update(@path id: string, @body body: MergePatchUpdate<Product>): Product;

  /** Delete a product */
  @delete delete(@path id: string): void;
}

